/**
 * 新增游戏等级
 * @param headNode
 * @param bodyNode
 * @param cb
 */
PageControl.prototype.addGameLevel = function(headNode, bodyNode, cb)
{
    var self = this;
    headNode.role = customerType.ADMIN.id;
    var backBodyNode = {};
    backBodyNode.id = bodyNode.id;
    backBodyNode.parent = bodyNode.parent;
    async.waterfall([
        function(cb){   //校验登录
            loginService.pageCheck(headNode, function(err, data){
                cb(err);
            })
        },
        function(cb)
        {
            var table = dc.pg.get("game");
            table.find({}, {}, function(err, data){
                if(data)
                {
                    backBodyNode.game = data.rst;
                }
                cb(err);
            });
        }
    ], function (err, result) {
        cb(err, backBodyNode);
    });
};

