var async = require('async');

var esut = require('easy_util');
var digestUtil = esut.digestUtil;
var pageUtil = esut.pageUtil;
var dateUtil = esut.dateUtil;

var service = require('service');
var loginService = service.loginService;

var config = require('config');
var ec = config.ec;

var dc = require('db').dc;

var cons = require('cons');
var customerType = cons.customerType;

var PageControl = function(){};

/**
 * 二层结构
 * @param headNode
 * @param bodyNode
 * @param cb
 */
PageControl.prototype.handle = function(headNode, bodyNode, cb)
{
    var self = this;
    var cmd = headNode.cmd;
    if(self[cmd[2]])
    {
        self[cmd[2]](headNode, bodyNode, cb);
    }
    else
    {
        cb(ec[100]);
    }
};

/**
 * 用户管理首页
 * @param headNode
 * @param bodyNode
 * @param cb
 */
PageControl.prototype.index = function(headNode, bodyNode, cb)
{
    var self = this;
    headNode.role = customerType.ADMIN.id;
    var backBodyNode = {};
    backBodyNode.id = bodyNode.id;
    backBodyNode.parent = bodyNode.parent;
    async.waterfall([
        function(cb){   //校验登录
            loginService.pageCheck(headNode, function(err, data){
                cb(err);
            })
        },
        function(cb)    //获取记录集
        {
            cb(null);
        }
    ], function (err, result) {
        cb(err, backBodyNode);
    });
};

/**
 * 用户列表
 * @param headNode
 * @param bodyNode
 * @param cb
 */
PageControl.prototype.listCustomer = function(headNode, bodyNode, cb)
{
    var self = this;
    headNode.role = customerType.ADMIN.id;
    var backBodyNode = {};
    backBodyNode.id = bodyNode.id;
    backBodyNode.parent = bodyNode.parent;

    backBodyNode.sort = {reg_time:-1};
    backBodyNode.limit = 10;
    pageUtil.parse(bodyNode, backBodyNode);

    var table = dc.pg.get("customer");
    async.waterfall([
        function(cb){   //校验登录
            loginService.pageCheck(headNode, function(err, data){
                cb(err);
            })
        },
        function(cb)    //获取记录集
        {
            var options = {sort:backBodyNode.sort, limit:backBodyNode.limit, offset:backBodyNode.skip};
            table.find(backBodyNode.cond, {}, options, function(err, data){
                if(data)
                {
                    for(var key in data.rst)
                    {
                        var set = data.rst[key];
                        set.channel = cons.channel.id[set.channel];
                        set.client_type = cons.clientType.id[set.client_type];
                        set.reg_type = cons.regType.id[set.reg_type];
                        set.status = cons.customerStatus.id[set.status];
                    }
                    backBodyNode.rst = data.rst;
                }
                cb(err);
            });
        },
        function(cb)    //获取记录数
        {
            table.count(backBodyNode.cond, function(err, data){
                if(data)
                {
                    backBodyNode.count = data.rst[0].count;
                }
                cb(err);
            });
        }
    ], function (err, result) {
        cb(err, backBodyNode);
    });
};

/**
 * 用户详情
 * @param headNode
 * @param bodyNode
 * @param cb
 */
PageControl.prototype.detail = function(headNode, bodyNode, cb)
{
    var self = this;
    headNode.role = customerType.ADMIN.id;
    var backBodyNode = {};
    backBodyNode.id = bodyNode.id;
    backBodyNode.parent = bodyNode.parent;
    backBodyNode.customerStatus = cons.customerStatus;
    var table = dc.pg.get("customer");
    async.waterfall([
        function(cb){   //校验登录
            loginService.pageCheck(headNode, function(err, data){
                cb(err);
            })
        },
        function(cb)    //获取用户信息
        {
            var cond = {id:bodyNode.customerId}
            table.find(cond, {}, function(err, data){
                if(data)
                {
                    for(var key in data.rst)
                    {
                        var set = data.rst[key];
                        set.channel = cons.channel.id[set.channel];
                        set.client_type = cons.clientType.id[set.client_type];
                        set.reg_type = cons.regType.id[set.reg_type];
                        set.reg_time = set.reg_time.substr(0, 19);
                    }
                    backBodyNode.customer = data.rst[0];
                }
                cb(err);
            });
        },
        function(cb)    //获取用户登录信息
        {
            var table = dc.pg.get("st_info");
            var cond = {id:bodyNode.customerId};
            table.find(cond, {}, function(err, data){
                if(data && data.rst.length > 0)
                {
                    backBodyNode.customer.last_active_device = data.rst[0].last_active_device;
                    backBodyNode.customer.last_active_time = data.rst[0].last_active_time.substr(0, 19);
                }
                cb(err);
            });
        }
    ], function (err, result) {
        cb(err, backBodyNode);
    });
};

var PageControl = new PageControl();
module.exports = PageControl;