var ErrCode = function()
{
    var self = this;
    self.data = [
        {code:0, description:"系统处理成功"},


        {code:100, description:"页面不存在"},
        {code:101, description:"记录不存在"},

        {code:1000, description:"用户不存在"},
        {code:1001, description:"不存在的接口"},
        {code:1002, description:"消息头格式错误"},
        {code:1003, description:"消息体格式错误"},
        {code:1004, description:"签名错误"},
        {code:1005, description:"不支持的密钥来源"},
        {code:1006, description:"用户名或者密码错误"},
        {code:1007, description:"未登录或者登录超时"},
        {code:1008, description:"用户名已经存在"},

        {code:2000, description:"投注格式错误"},
        {code:2001, description:"不支持的游戏"},
        {code:2002, description:"金额错误"},


        {code:9999, description:"服务器内部错误"}
    ];

    for(var i = 0; i < self.data.length; i++)
    {
        var set = self.data[i];
        self[set.code] = set;
    }
};

module.exports = new ErrCode();