var exports = {};
var target = 'dev';

var argv = process.argv;
var kvs = {};
for(var key in argv)
{
    if(key > 1)
    {
        var kv = argv[key].split("=");
        kvs[kv[0]] = kv[1];
    }
}
if(kvs.target)
{
    target = kvs.target;
}

//runtime target
exports.target = target;

//config db basic type
var dbs = {
    'devPg':
    {
        url:"host=127.0.0.1 dbname=mcp_game user=postgres",
        cmPoolSize:20,
        trPoolSize:5,
        esPoolSize:1
    }
}

if(target == 'dev')
{
    exports.pg = dbs.devPg;
}
else if(target == 'test')
{

}
else if(target == 'run')
{

}

//if user hasn't operation in half a hour, the key will be expired.
exports.loginExpiredSeconds = 300*60;


module.exports = exports;


