var Cons = function(){
    var self = this;
    self.data = [
        {id:0, code:'GUEST', des:"游客"},
        {id:1, code:'NORMAL', des:"普通用户"},
        {id:2, code:'ADMIN', des:"管理员"}
    ];

    self.id = {};

    for(var i = 0; i < self.data.length; i++)
    {
        var set = self.data[i];
        self[set.code] = set;

        self.id[set.id] = set;
    }
};
module.exports = new Cons();

