var Cons = function(){
    var self = this;
    self.data = [
        {id:0, code:'DIR', mime:"DIR", des:"文件夹"},
        {id:50, code:'UNKOWN', mime:"UNKOWN", des:"未知"},
        {id:100, code:'HTML', mime:"text/html", des:"网页"},
        {id:200, code:'JPEG', mime:"image/jpeg", des:"JPEG图片"},
        {id:300, code:'PNG', mime:"image/png", des:"PNG图片"}
    ];

    self.id = {};
    self.mime = {};

    for(var i = 0; i < self.data.length; i++)
    {
        var set = self.data[i];
        self[set.code] = set;

        self.mime[set.mime] = set;
        self.id[set.id] = set;
    }
};
module.exports = new Cons();

