var Cons = function(){
    var self = this;
    self.data = [
        {id:100, code:'UNHANDLE', des:"待处理"},
        {id:200, code:'HANDLING', des:"已受理"},
        {id:300, code:'HANDLED', des:"已完结"}
    ];

    self.id = {};

    for(var i = 0; i < self.data.length; i++)
    {
        var set = self.data[i];
        self[set.code] = set;

        self.id[set.id] = set;
    }
};
module.exports = new Cons();

