/**
 * 金币账户科目
 * @constructor
 */
var Cons = function(){
    var self = this;
    self.data = {
        'in':[
            {id:1, code:'CHARGE', des:"充值"},
            {id:2, code:'SYSTEM_GIVE', des:"系统赠送"},
            {id:3, code:'HIT', des:"中奖"},
            {id:4, code:'REGISTER', des:"新注册奖励"},
            {id:5, code:'LOGIN', des:"签到奖励"},
            {id:6, code:'TIME', des:"时间积累奖励"}
        ],
        'out':[
            {id:-1, code:'BET', des:"投注"},
            {id:-2, code:'EXCHANGE', des:"兑换"}
        ]
    };

    self.id = {};

    //账户收入
    var inArray = self.data.in;
    for(var i = 0; i < inArray.length; i++)
    {
        var set = inArray[i];
        self[set.code] = set;

        self.id[set.id] = set;
    }

    //账户支出
    var outArray = self.data.out;
    for(var i = 0; i < outArray.length; i++)
    {
        var set = outArray[i];
        self[set.code] = set;

        self.id[set.id] = set;
    }
};
module.exports = new Cons();

