var Cons = function(){
    var self = this;
    self.data = [
        {id:0, code:'SYSTEM', des:"系统"},
        {id:1, code:'Guest', des:"游客"},
        {id:2, code:'Wenxin', des:"微信"}
    ];

    self.id = {};

    for(var i = 0; i < self.data.length; i++)
    {
        var set = self.data[i];
        self[set.code] = set;

        self.id[set.id] = set;
    }
};
module.exports = new Cons();

