var Cons = function(){
    var self = this;
    self.data = [
        {id:0, code:'REPEAT', des:"可重复"},
        {id:1, code:'UNREPEAT', des:"游戏内一次性"},
        {id:2, code:'OUTSOURCE', des:"推广一次性"}
    ];

    self.id = {};

    for(var i = 0; i < self.data.length; i++)
    {
        var set = self.data[i];
        self[set.code] = set;

        self.id[set.id] = set;
    }
};
module.exports = new Cons();

