var config = require('config');
var ec = config.ec;

/**
 * admin只是一个中间层，因为管理后台页面有二层结构
 * @type {exports}
 */
var admin = require('admin');

var PageControl = function(){};

PageControl.prototype.handle = function(headNode, bodyNode, cb)
{
    var self = this;
    var cmd = headNode.cmd;
    if(self[cmd[1]])
    {
        self[cmd[1]](headNode, bodyNode, cb);
    }
    else
    {
        cb(ec[100]);
    }
};

/**
 * 用户管理
 * @param headNode
 * @param bodyNode
 * @param cb
 */
PageControl.prototype.user = function(headNode, bodyNode, cb)
{
    admin.userCtl.handle(headNode, bodyNode, cb);
};

