var animal = require('game/animal');
var turntable = require('game/turntable');
var bonus = require('game/bonus');

var config = require('config');
var ec = config.ec;

var esut = require('easy_util');
var log = esut.log;

var Factory = function(){};

Factory.prototype.do = function(ticket, cb)
{
    var self = this;
    var method = self[ticket.game_code];
    if(method)
    {
        method(ticket, cb);
    }
    else
    {
        log.error(ec[2001]);
        cb(ec[2001]);
    }
};

/**
 * 猜动物游戏
 * @param ticket
 * @param cb
 */
Factory.prototype.G01 = function(ticket, cb)
{
    animal.check.do(ticket, cb);
};

Factory.prototype.G02 = function(ticket, cb){
    turntable.check.do(ticket, cb);
};

Factory.prototype.bonus = function (ticket, cb) {
    bonus.check.do(ticket, cb);
};

module.exports = new Factory();
