var animal = require('game/animal');

var config = require('config');
var ec = config.ec;

var esut = require('easy_util');
var log = esut.log;

var ValidateFactory = function(){};

ValidateFactory.prototype.do = function(ticket, cb)
{
    var self = this;
    var method = self[ticket.game_code];
    if(method)
    {
        method(ticket, cb);
    }
    else
    {
        log.error(ec[2001]);
        cb(ec[2001]);
    }
};

/**
 * 猜动物游戏
 * @param ticket
 * @param cb
 */
ValidateFactory.prototype.G01 = function(ticket, cb)
{
    animal.validate.do(ticket, cb);
};

module.exports = new ValidateFactory();
