/**
 * 系统奖励
 * @param headNode
 * @param bodyNode
 * @param cb
 */
Control.prototype.handleU13 = function(headNode, bodyNode, cb){
    var backBody = {};
    var userId = parseInt(headNode.userId);
    async.waterfall([
        function(cb){
            if(bodyNode.giftType==1){
                var table = dc.pg.get("gold_log");
                var userId = parseInt(headNode.userId);
                var sql = "SELECT * FROM gold_log WHERE customer_id=" + userId + " AND subject_id=5 AND TO_CHAR(create_time, 'YYYY-MM-DD')=TO_CHAR(now()-interval '1D', 'YYYY-MM-DD')";
                table.execute(sql, function(err, data){
                    var user = dc.pg.get("customer");
                    if(err || data.rst.length < 1){
                        user.update({id:userId}, {$set:{sign_days:1}},function(err,data){});
                    }else{
                        user.update({id:userId}, {$inc:{sign_days:1}},{ret:{sign_days:1}},function(err,data){});
                    }
                    cb(null);
                });
            }
        },
        function(cb)
        {
            var table = dc.pg.get("customer");
            var cond = {id:userId};
            var ret = {
                gold:1,
                sign_days:1
            };
            var doc = {
                $inc: {
                    gold:bodyNode.amount
                }
            };

            table.update(cond, doc, {ret:ret}, function(err, data){
                if(data && data.rst.length > 0)
                {
                    backBody.customer = {gold:data.rst[0].gold, sign_days:data.rst[0].sign_days};
                    cb(null);
                }
                else
                {
                    cb(err);
                }
            });
        },
//        function(cb){
//
//        },
        function(cb)    //记录账户流水
        {
            var table = dc.pg.get("gold_log");
            var goldLog = {
                amount:bodyNode.amount,
                before:backBody.customer.gold - bodyNode.amount,
                after:backBody.customer.gold,
                subject_id:bodyNode.giftType + 4,
                remark:cons.goldSubject.SYSTEM_GIVE.des + ", 类型:" + bodyNode.giftType,
                customer_id:userId,
                order_id:bodyNode.uuid
            };
            table.save(goldLog, function(err, data){
                cb(err);
            });
        }
    ], function (err, rst) {
        cb(err, backBody);
    });
};

/**
 * 修改用户信息
 * @param headNode
 * @param bodyNode
 * @param cb
 */
Control.prototype.handleU14 = function(headNode, bodyNode, cb){
    var backBody = {};
    var userId = parseInt(headNode.userId);
    var table = dc.pg.get("customer");
    var cond = {id:userId};
    switch(bodyNode.option){
        case "ROLLCHANGE":
            if (typeof bodyNode.nickname != 'string') bodyNode.nickname="";
            var doc = {$set:{username:bodyNode.username, nickname:bodyNode.nickname, password:bodyNode.password, type:1}};
            table.update(cond, doc, function(err, data) {
                if (data && data.rst.length > 0) {
                    backBody.customer = {username: bodyNode.username, password: bodyNode.password, nickname: bodyNode.nickname};
                    cb(null);
                }
                else {
                    cb(err);
                }
            });
            break;
        case "PASSWORD":
            table.update(cond, {$set:{password:bodyNode.password}}, function(err, data) {
                if (data && data.rst.length > 0) {
                    backBody.customer = {userId:userId, password: bodyNode.password};
                    cb(null);
                }
                else {
                    cb(err);
                }
            });
            break;
        case "NICKNAME":
            table.update(cond, {$set:{nickname:bodyNode.nickname}}, function(err, data) {
                if (data && data.rst.length > 0) {
                    backBody.customer = {userId:userId, nickname: bodyNode.nickname};
                    cb(null);
                }
                else {
                    cb(err);
                }
            });
            break;
        default:
            cb(ec[1001]);
    }
};

