/**
 * 初始化一个数据库列
 * @param name
 * @param type
 * @param desc
 * @constructor
 */
var Column = function(name, type, length, desc){
    var self = this;
    self.name = name;
    self.type = type;
    self.length = length;
    self.desc = desc;
};

/**
 * 转换成数据库中一列的描述
 */
Column.prototype.toString = function()
{
    var self = this;
    var sql = self.name + " " + self.type;
    if(self.length > 0)
    {
        sql += "(" + self.length + ")";
    }
    sql += " " + self.desc;
    return sql;
};

module.exports = Column;

