#include "PgWorkData.h"

PgWorkData::PgWorkData() {
	this->error = false;
	this->errorMsg = NULL;
	//vs_log_info("destroy PgWorkData");
}

PgWorkData::~PgWorkData() {
	vs_string_destroy(this->errorMsg);
}

PgConnBuildWorkData::PgConnBuildWorkData() {
	this->code = NULL;
	this->conn = NULL;
}

PgConnBuildWorkData::~PgConnBuildWorkData() {
	vs_string_destroy(this->code);
}

PgConnExecuteWorkData::PgConnExecuteWorkData() {
	this->cmd = NULL;
	this->conn = NULL;
	this->pr = NULL;
}

PgConnExecuteWorkData::~PgConnExecuteWorkData() {
	vs_string_destroy(this->cmd);
	delete this->pr;
	//vs_log_info("destroy PgConnExecuteWorkData");
}



