#include <stdio.h>
#include <stdlib.h>

#include "VsUtil.h"

/**
 * str 转换成long型数据
 */
long vs_util_str_to_long(struct VsString* str)
{
    return atol(str->pt);
}

/**
 * str 转换成long型数据
 */
long vs_util_str_to_long(const char* str)
{
	return atol(str);
}

double vs_util_str_to_double(struct VsString* str)
{
    return strtod(str->pt, NULL);
}

/**
 * 获得a(m, n)的值
 */
long vs_util_getA(long m, long n)
{
	long value = 1;
	for (long i = 0; i < n; i++) {
		value = value * (m - i);
	}
	return value;
}

/**
 * 获得c(m, n)的值
 */
long vs_util_getC(long m, long n)
{
	if ((m < n) || (n < 0)) {
		return 0;
	}
	return vs_util_getA(m, n) / vs_util_getA(n, n);
}
